asset_type_detection.algorithm:
function detect_asset_type(symbol, market_cap, sector) {
  if (symbol === 'BTC') return 'BTC';
  else if (symbol === 'ETH') return 'ETH';
  else if (market_cap > 10000000000) return 'LARGE_CAP_ALT';
  else if (market_cap > 1000000000) return 'MID_CAP_ALT';
  else if (market_cap > 100000000) return 'SMALL_CAP_ALT';
  else if (sector === 'MEME') return 'MEME_COIN';
  else return 'SMALL_CAP_ALT';
}
